(264, 79, '_form', '<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label\">\n    <label>Категорія</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [text* place-category]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label\">\n    <label>Назва закладу</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [text* place-name]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label form-label-textarea\">\n    <label>Опис закладу</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [textarea form-message]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label\">\n    <label>Логотип</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [file place-logo limit:102400]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label\">\n    <label>Місто</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [text* place-city]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label\">\n    <label>Адреса</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [text* place-address]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label form-label-map\">\n    <label>Місце розташування</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [map place-map \"zoom:11;clat:50.45270299167948;clng:30.51702190035821;lat:50.450470;lng:30.523504\"]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label\">\n    <label>Сайт</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [url place-web]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label\">\n    <label>Телефон</label>\n  </div>\n  <div class=\"col-md-7 form-field\">\n    [tel place-tel]\n  </div>\n</div>\n\n<div class=\"row form-row\">\n  <div class=\"col-md-5 form-label\">\n    <label>Час роботи</label>\n  </div>\n  <div